
const fs = require('fs');
const readline = require('readline');
const { exec } = require('child_process');

// Create interface for reading input
const rl = readline.createInterface({
  input: process.stdin,
  output: process.stdout
});

console.log('\nüöÄ WELCOME TO NUMBER QUEST PAYMENT SERVER SETUP üöÄ\n');
console.log('This script will help you set up your PayPal integration in just a few steps.\n');

// Ask for PayPal credentials
rl.question('Enter your PayPal Client ID: ', (clientId) => {
  rl.question('Enter your PayPal Client Secret: ', (clientSecret) => {
    console.log('\nSetting up your server...');
    
    // Create .env file
    const envContent = `
# PayPal API Credentials
PAYPAL_CLIENT_ID=${clientId}
PAYPAL_CLIENT_SECRET=${clientSecret}

# Environment - set to 'production' when deploying to production
NODE_ENV=development

# Port for the server
PORT=3000
`;

    // Write to .env file
    fs.writeFileSync('.env', envContent.trim());
    console.log('‚úÖ Created .env file with your PayPal credentials');
    
    // Install dependencies
    console.log('\nInstalling server dependencies...');
    exec('npm install', (error, stdout, stderr) => {
      if (error) {
        console.error('‚ùå Error installing dependencies:', error);
        rl.close();
        return;
      }
      
      console.log('‚úÖ Dependencies installed successfully');
      console.log('\nüéÆ SETUP COMPLETE! üéÆ');
      console.log('\nTo start your server, run:');
      console.log('npm run dev\n');
      console.log('You should see "Server running on port 3000" when it starts.\n');
      
      rl.close();
    });
  });
});

